const { createLogger } = require('../utils/index.js');
const { get, set } = require('../utils/cacheClient.js');
const authenticate = async (req, res, next) => {
  try {
    const logger = createLogger('authMiddleware');
    const cachedUser = await get('user');
    if (cachedUser) {
      req.user = cachedUser;
      next();
    } else {
