const { Pool } = require('pg');
const { getPools } = require('../database/index');
const { createLogger } = require('../utils/index');

const logger = createLogger('usersModels');

const createUsersModels = () => {
  const pool = getPools();

  const createRecord = async (data) => {
    try {
      const result = await pool.query(`INSERT INTO users (name, email) VALUES ($1, $2) RETURNING *`, [data.name, data.email]);
      return result.rows[0];
    } catch (error) {
      logger.error(error);
      throw error;
    }
  };

  const findAllRecords = async () => {
    try {
      const result = await pool.query(`SELECT * FROM users WHERE deleted_at IS NULL`);
      return result.rows;
    } catch (error) {
      logger.error(error);
      throw error;
    }
  };

  const findByIdRecord = async (id) => {
    try {
      const result = await pool.query(`SELECT * FROM users WHERE id = $1 AND deleted_at IS NULL`, [id]);
      return result.rows[0];
    } catch (error) {
      logger.error(error);
      throw error;
    }
  };

  const updateByIdRecord = async (id, data) => {
    try {
      const result = await pool.query(`UPDATE users SET name = $1, email = $2 WHERE id = $3 RETURNING *`, [data.name, data.email, id]);
      return result.rows[0];
    } catch (error) {
      logger.error(error);
      throw error;
    }
  };

  const deleteByIdRecord = async (id) => {
    try {
      await pool.query(`UPDATE users SET deleted_at = NOW() WHERE id = $1`, [id]);
    } catch (error) {
      logger.error(error);
      throw error;
    }
  };

  const countRecords = async () => {
    try {
      const result = await pool.query(`SELECT COUNT(*) FROM users WHERE deleted_at IS NULL`);
      return result.rows[0].count;
    } catch (error) {
      logger.error(error);
      throw error;
    }
  };

  return { createRecord, findAllRecords, findByIdRecord, updateByIdRecord, deleteByIdRecord, countRecords };
};

module.exports = { createUsersModels };

