const { Pool } = require('pg');

const sslConfig = process.env.NODE_ENV === 'production' && process.env.DB_SSL !== 'false'
  ? { rejectUnauthorized: false }
  : false;

let pool = null;

const createPools = () => {
  if (!pool) {
    pool = new Pool({
      user: process.env.DB_USER || 'postgres',
      host: process.env.DB_HOST || 'localhost',
      database: process.env.DB_NAME || 'sdfsdf_3',
      password: process.env.DB_PASSWORD,
      port: parseInt(process.env.DB_PORT || '5432', 10),
      ssl: sslConfig,
      max: parseInt(process.env.DB_POOL_MAX || '10', 10),
      min: parseInt(process.env.DB_POOL_MIN || '2', 10),
      idleTimeoutMillis: parseInt(process.env.DB_POOL_IDLE || '10000', 10),
      connectionTimeoutMillis: 2000,
    });

    pool.on('error', (err) => {
      console.error('Unexpected database pool error:', err);
    });
  }
  return pool;
};

const getPools = () => {
  return pool ? pool : createPools();
};

const closePools = async () => {
  if (pool) {
    await pool.end();
    pool = null;
  }
};

module.exports = { createPools, getPools, closePools };

const { createPools } = require('./connections');
const fs = require('fs');
const path = require('path');

async function runMigrations() {
  const pool = createPools();

  try {
    console.log('Starting migrations...');
    const migrationsDir = path.join(__dirname, 'migrations');

    if (!fs.existsSync(migrationsDir)) {
      console.log('No migrations directory');
      return;
    }

    const files = fs.readdirSync(migrationsDir).filter(f => f.endsWith('.sql')).sort();

    await pool.query(`CREATE TABLE IF NOT EXISTS schema_migrations (
      id SERIAL PRIMARY KEY,
      filename VARCHAR(255) UNIQUE NOT NULL,
      executed_at TIMESTAMPTZ NOT NULL DEFAULT CURRENT_TIMESTAMP
    )`);

    for (const file of files) {
      const check = await pool.query('SELECT filename FROM schema_migrations WHERE filename = $1', [file]);
      if (check.rows.length > 0) {
        console.log(`  Skipping ${file}`);
        continue;
      }

      console.log(`Running ${file}`);
      const sql = fs.readFileSync(path.join(migrationsDir, file), 'utf8');

      await pool.query('BEGIN');
      try {
        const statements = sql.split(';').map(s => s.trim()).filter(s => s.length > 0 && !s.startsWith('--'));
        for (const stmt of statements) {
          if (stmt.trim()) await pool.query(stmt);
        }
        await pool.query('INSERT INTO schema_migrations (filename) VALUES ($1)', [file]);
        await pool.query('COMMIT');
        console.log(` ${file}`);
      } catch (error) {
        await pool.query('ROLLBACK');
        throw error;
      }
    }
    console.log(' Migrations complete');
  } catch (error) {
    console.error('Migration failed:', error);
    throw error;
  }
}

if (require.main === module) {
  runMigrations()
    .then(() => process.exit(0))
    .catch(() => process.exit(1));
}

module.exports = { runMigrations };

const { createPools, closePools } = require('./connections');

async function seedDatabase() {
  const pool = createPools();
  try {
    console.log('Starting seeding...');
    const tables = ["users","categories","tasks","task_tags","settings","audit_logs","permissions","priority_levels"];

    for (const table of tables) {
      const result = await pool.query(`SELECT COUNT(*) as count FROM ${table}`);
      if (parseInt(result.rows[0].count) > 0) {
        console.log(`${table} has data, skipping`);
        continue;
      }
      console.log(`Seeding ${table}`);
    }
    console.log(' Seeding complete');
  } catch (error) {
    console.error('Seeding failed:', error);
    throw error;
  } finally {
    await closePools();
  }
}

if (require.main === module) {
  seedDatabase()
    .then(() => process.exit(0))
    .catch(() => process.exit(1));
}

module.exports = { seedDatabase };

